import { useEffect } from 'react';

export default function ForexPair({ setVisitedForexPair, setSelectedCurrency, setAlternativeCurrencies, selectedCurrency, alternativeCurrencies }) {
  useEffect(() => {
    // Ustawiamy, że strona została odwiedzona
    setVisitedForexPair(true);
  }, [setVisitedForexPair]);

  const currencies = [
    { id: 'PLN', name: 'Polski złoty', flag: 'https://upload.wikimedia.org/wikipedia/en/1/12/Flag_of_Poland.svg' },
    { id: 'EUR', name: 'Euro', flag: 'https://upload.wikimedia.org/wikipedia/commons/b/b7/Flag_of_Europe.svg' },
    { id: 'USD', name: 'Dolar amerykański', flag: 'https://upload.wikimedia.org/wikipedia/en/a/a4/Flag_of_the_United_States.svg' },
    { id: 'GBP', name: 'Funt brytyjski', flag: 'https://upload.wikimedia.org/wikipedia/en/a/ae/Flag_of_the_United_Kingdom.svg' },
  ];

  const handleCurrencyClick = (currencyId) => {
    if (selectedCurrency === null) {
      setSelectedCurrency(currencyId);  // Ustaw walutę bazową
    } else {
      // Sprawdź, czy waluta alternatywna nie jest taka sama jak bazowa
      if (currencyId === selectedCurrency) {
        alert("You can't choose the same currency twice!");
      } else {
        // Sprawdź, czy waluta alternatywna jest już wybrana, jeśli tak, usuń ją
        if (alternativeCurrencies.includes(currencyId)) {
          setAlternativeCurrencies(alternativeCurrencies.filter((currency) => currency !== currencyId));
        } else {
          setAlternativeCurrencies([...alternativeCurrencies, currencyId]);  // Dodaj walutę alternatywną
        }
      }
    }
  };

  const handleNextClick = () => {
    // Sprawdź, czy waluta bazowa i przynajmniej dwie waluty alternatywne zostały wybrane
    if (selectedCurrency && alternativeCurrencies.length >= 2) {
      alert(`Selected base currency: ${selectedCurrency}, alternative currencies: ${alternativeCurrencies.join(', ')}`);
    } else {
      alert('Please select a base currency and at least two alternative currencies!');
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center text-white p-4">
      <h1 className="text-5xl font-bold mb-6">Forex Pairs Overview</h1>

      <p className="text-lg text-gray-300 text-center max-w-2xl">
        Choose your base and alternative currencies (select at least two alternatives)
        <span> (Selected: {selectedCurrency ? selectedCurrency : 'None'}, Alternatives: {alternativeCurrencies.join(', ')})</span>
      </p>

      <div className="m-10">
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 justify-center">
          {currencies.map((currency) => (
            <div
              key={currency.id}
              className={`relative flex flex-col items-center cursor-pointer transition-transform transform hover:scale-105 ${
                (selectedCurrency === currency.id || alternativeCurrencies.includes(currency.id))
                  ? 'bg-indigo-500'
                  : 'bg-gray-700'
              } w-40 h-48 p-4 rounded-lg`}
              onClick={() => handleCurrencyClick(currency.id)}
            >
              <div className="w-24 h-24 flex items-center justify-center">
                <img src={currency.flag} alt={currency.name} className="w-16 h-16 rounded-full" />
              </div>
              <p className="text-lg mt-4 text-center">{currency.name} ({currency.id})</p>
            </div>
          ))}
        </div>
      </div>

      <button
        onClick={handleNextClick}
        className="bg-sky-500 hover:bg-sky-700 text-white font-bold py-4 px-8 rounded-full text-xl shadow-lg transform hover:scale-105 transition-transform duration-300"
      >
        Dalej
      </button>
    </div>
  );
}
