import { useState } from 'react';
import { useNavigate } from 'react-router-dom';  // Importuj useNavigate z react-router-dom

export default function ForexPair() {
  const navigate = useNavigate();  // Inicjalizuj funkcję navigate

  const [selectedCurrency, setSelectedCurrency] = useState(localStorage.getItem('selectedCurrency') || null);  // Wybrana waluta bazowa
  const [alternativeCurrencies, setAlternativeCurrencies] = useState(
    JSON.parse(localStorage.getItem('alternativeCurrencies')) || []
  );  // Wybrane waluty alternatywne
  const [step, setStep] = useState(1);  // Krok procesu

  const currencies = [
    { id: 'PLN', name: 'Polski złoty', flag: 'https://upload.wikimedia.org/wikipedia/en/1/12/Flag_of_Poland.svg' },
    { id: 'EUR', name: 'Euro', flag: 'https://upload.wikimedia.org/wikipedia/commons/b/b7/Flag_of_Europe.svg' },
    { id: 'USD', name: 'Dolar amerykański', flag: 'https://upload.wikimedia.org/wikipedia/en/a/a4/Flag_of_the_United_States.svg' },
    { id: 'GBP', name: 'Funt brytyjski', flag: 'https://upload.wikimedia.org/wikipedia/en/a/ae/Flag_of_the_United_Kingdom.svg' },
  ];

  const handleCurrencyClick = (currencyId) => {
    if (step === 1) {
      setSelectedCurrency(currencyId);  // Zapisz wybraną walutę bazową
    } else {
      if (currencyId === selectedCurrency) {
        alert("You can't choose the same currency twice!");
      } else {
        if (alternativeCurrencies.includes(currencyId)) {
          setAlternativeCurrencies(alternativeCurrencies.filter((currency) => currency !== currencyId));  // Usuń walutę, jeśli została wcześniej wybrana
        } else {
          setAlternativeCurrencies([...alternativeCurrencies, currencyId]);  // Dodaj nową walutę alternatywną
        }
      }
    }
  };

  const handleNextClick = () => {
    if (step === 1) {
      if (selectedCurrency) {
        localStorage.setItem('selectedCurrency', selectedCurrency);  // Zapisz wybraną walutę bazową
        setStep(2);  // Przejdź do wyboru walut alternatywnych
      } else {
        alert('Proszę wybrać walutę!');
      }
    } else {
      if (alternativeCurrencies.length >= 2) {
        localStorage.setItem('alternativeCurrencies', JSON.stringify(alternativeCurrencies));  // Zapisz wybrane waluty alternatywne
        alert(`Wybrane waluty alternatywne: ${selectedCurrency}/${alternativeCurrencies.join(', ')} dalej modele itd trzeba zrobic`);
        navigate('/models');  // Przekieruj na stronę "Models"
      } else {
        alert('Proszę wybrać co najmniej dwie waluty alternatywne!');
      }
    }
  };

  const handleBackClick = () => {
    setStep(1);  // Powrót do kroku 1
  };

  const chosenCurrencies = selectedCurrency
    ? alternativeCurrencies.length > 0
      ? `${selectedCurrency}/${alternativeCurrencies.join(', ')}`
      : selectedCurrency
    : null;

  return (
    <div className="min-h-screen flex flex-col items-center text-white p-4">
      <h1 className="text-5xl font-bold mb-6">Forex Pairs Overview</h1>

      {step === 1 ? (
        <p className="text-lg text-gray-300 text-center max-w-2xl">
          Choose your base currency
          {chosenCurrencies && <span> (Chosen: {chosenCurrencies})</span>}
        </p>
      ) : (
        <p className="text-lg text-gray-300 text-center max-w-2xl">
          Choose your alternative currencies (select at least two)
          {chosenCurrencies && <span> (Chosen: {chosenCurrencies})</span>}
        </p>
      )}

      <div className="m-10">
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 justify-center">
          {currencies.map((currency) => (
            <div
              key={currency.id}
              className={`relative flex flex-col items-center cursor-pointer transition-transform transform hover:scale-105 ${
                (step === 1 && selectedCurrency === currency.id) ||
                (step === 2 && alternativeCurrencies.includes(currency.id))
                  ? 'bg-indigo-500'
                  : 'bg-gray-700'
              } w-40 h-48 p-4 rounded-lg`}
              onClick={() => handleCurrencyClick(currency.id)}
            >
              <div className="w-24 h-24 flex items-center justify-center">
                <img src={currency.flag} alt={currency.name} className="w-16 h-16 rounded-full" />
              </div>
              <p className="text-lg mt-4 text-center">{currency.name} ({currency.id})</p>

              {((step === 1 && selectedCurrency === currency.id) ||
                (step === 2 && alternativeCurrencies.includes(currency.id))) && (
                <div className="absolute top-0 right-0 bg-green-500 w-6 h-6 rounded-full flex items-center justify-center">
                  <svg
                    className="w-4 h-4 text-white"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                    xmlns="http://www.w3.org/2000/svg"
                  >
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7" />
                  </svg>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>

      <div className="flex space-x-4">
        {step === 2 && (
          <button
            onClick={handleBackClick}
            className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-4 px-8 rounded-full text-xl shadow-lg transform hover:scale-105 transition-transform duration-300"
          >
            Wróć
          </button>
        )}
        <button
          onClick={handleNextClick}
          className="bg-sky-500 hover:bg-sky-700 text-white font-bold py-4 px-8 rounded-full text-xl shadow-lg transform hover:scale-105 transition-transform duration-300"
        >
          Dalej
        </button>
      </div>
    </div>
  );
}
