import Header from '../components/Header';
import Home from './pages/Home';
import ForexPair from './pages/ForexPair';
import Models from './pages/Models.js';
import { createBrowserRouter, RouterProvider, Outlet } from 'react-router-dom';
import { useState } from 'react';

export default function Router() {
  const [visitedForexPair, setVisitedForexPair] = useState(false);  // Śledzenie, czy ForexPair zostało odwiedzone
  const [selectedCurrency, setSelectedCurrency] = useState(null);  // Waluta bazowa
  const [alternativeCurrencies, setAlternativeCurrencies] = useState([]);  // Waluty alternatywne

  const Layout = () => {
    return (
      <>
        <Header />
        <Outlet />
      </>
    );
  };

  const BrowserRoutes = createBrowserRouter([
    {
      path: "/",
      element: <Layout />,
      children: [
        {
          path: "/",
          element: <Home />
        },
        {
          path: "/forex-pair",
          element: <ForexPair
            setVisitedForexPair={setVisitedForexPair}
            setSelectedCurrency={setSelectedCurrency}
            setAlternativeCurrencies={setAlternativeCurrencies}
            selectedCurrency={selectedCurrency}
            alternativeCurrencies={alternativeCurrencies}
          />
        },
        {
          path: "/models",
          element: <Models
            visitedForexPair={visitedForexPair}
            selectedCurrency={selectedCurrency}
            alternativeCurrencies={alternativeCurrencies}
          />
        }
      ]
    }
  ]);

  return (
    <RouterProvider router={BrowserRoutes} />
  );
}
